import React, { useState, useEffect } from "react";
import { useInput } from './input-hook';
import {getMenus} from '../utils/requests'

export function MainForm(props) {
    const { value:diningHall, bind:bindDiningHall, reset:resetDiningHall } = useInput('');
    const { value:date, bind:bindDate, reset:resetDate } = useInput('');    
    const { value:meal, bind:bindMeal, reset:resetMeal } = useInput('');    

    // const { value:lastName, bind:bindLastName, reset:resetLastName } = useInput('');    

    const [menus, setMenus] = useState([{category: 
        [
        {
            name: "",
            menuItem: [1,2,3]
        }
        ]
    }]);
    let [foodDisplay, setFoodDisplay] = useState({
        "Soup": {
            "First Soup": [
                "Chicken", "Beef"
            ],
            "Second Soup": [
                "Veggies", "Taro"
            ]
        }
    })
    let [foodAmounts, setFoodAmounts] = useState({
        "Chicken": 1, 
        "Beef": 2
    })
    const [foodFacts, setFoodFacts] = useState({
        "Chicken": {
            "Calories": 300, 
            "Protein": 20
        },
        "Beef": {
            "Calories": 250,
            "Protein": 25,
        }
    })

    const handleSubmit = (evt) => {
        evt.preventDefault();
        // alert(`Submitting Name ${value}`);
        // resetDiningHall();
        // resetDate();
        // resetMeal();
    }

    const fetchMenus = async (diningHall, date, meal) => {
        diningHall = diningHall.replace(' ', '%20')
        meal = meal.toUpperCase();
        const data = await getMenus(diningHall, date, meal)
        setMenus(data)
    }

    const amountInput = (val) => {
        return (
            <label>
                {val}:
                <input type="number" onChange={num => (
                    foodAmounts.val = num
                )} />
            </label>
        )
    }

    // useEffect(() => {
    //     // Update the document title using the browser API
    //     fetchMenus("Bursley", "2022-01-11", "lunch")
    // }, [fetchMenus]);

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>
                    Dining Hall:
                    <input type="text" {...bindDiningHall} />
                </label>
                <label>
                    Date:
                    <input type="text" {...bindDate} />
                </label>
                <label>
                    Meal:
                    <input type="text" {...bindMeal} />
                </label>
                <br></br>
                <button onClick={() => fetchMenus(diningHall, date, meal)}>Get Menus</button>
            </form>
            {
                // let url = 
                menus[0].category.map((categoryItem, index) => (
                    <div key={index}>
                        {categoryItem.name}
                    </div>
                ))
            }
            {
                // tempFoodDisplay = {}
                menus[0].category.map((categoryItem, index) => {
                    foodAmounts[categoryItem.menuItem[0].name] = 0
                    let tempCategory = categoryItem.name
                    let tempCategoryObject = {}
                    tempCategoryObject[tempCategory] = []
                    categoryItem.menuItem.map((menuItem, index) => {
                        tempCategoryObject[tempCategory].push(menuItem.name)
                    })
                    foodDisplay = (Object.assign(foodDisplay, tempCategoryObject))
                })
            }
            {
                Object.entries(foodDisplay).map(([key, value]) => (
                    Object.entries(value).map(([key2, value2]) => (
                        <div key={key2}>{value2}</div>
                    ))
                ))
            }
            <form>
                {
                    Object.entries(foodDisplay).map(([key, value]) => (
                        Object.entries(value).map(([key2, value2]) => (
                            amountInput(value2)
                        ))
                    ))
                }
            </form>
            {calculateTotal()}
            <div></div>
        </div>
    );
}
