import React, { useState, useEffect } from "react";
import { useInput } from './input-hook';
import {getMenus} from '../utils/requests'

export function MainForm(props) {
    const { value:firstName, bind:bindFirstName, reset:resetFirstName } = useInput('');
    const { value:lastName, bind:bindLastName, reset:resetLastName } = useInput('');    
    // const { value:lastName, bind:bindLastName, reset:resetLastName } = useInput('');    

    const [menus, setMenus] = useState([{category: 
        [
        {name: "chicken"}, {name: "beef"}
        ]
    }]);

    const handleSubmit = (evt) => {
        evt.preventDefault();
        // alert(`Submitting Name ${value}`);
        resetFirstName();
        resetLastName();
    }

    const fetchMenus = async () => {
        const data = await getMenus("01", "Bursley", "LUNCH")
        setMenus(data)
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
            <label>
                Name:
                <input type="text" {...bindFirstName} />
            </label>
            <label>
                Dining Hall:
                <input type="text" {...bindLastName} />
            </label>
            <br></br>
            <button onClick={() => fetchMenus()}>Get Menus</button>
            </form>
            {
                menus[0].category.map((categoryItem, index) => (
                    <div key={index}>
                        {categoryItem.name}
                    </div>
                ))
            }
        </div>
    );
}
