import React, { useState, useEffect } from "react";
import { Calculate } from "./Calculate";

export function FoodItem(props) {
    const [isActive, setActive] = useState("false");
    let val = props.val;

    return (
        <div>
            <label key={val}>
                <a onClick={
                    () => {
                        console.log(isActive)
                        setActive(!isActive);
                    }
                }>
                    {val}:
                </a>
                <input className="amount-form-single" type="number" onChange={num => {
                    // console.log(num.target.value)
                    let tempFoodAmounts = props.foodAmounts
                    tempFoodAmounts[val] = num.target.value
                    // console.log(tempFoodAmounts)
                    props.setFoodAmounts(tempFoodAmounts)
                }} />
            </label>
            <div className={isActive ? "show" : "hide"}>
                <ul style={{"margin-top": 0}}>
                    <li>Serving Size: {props.foodFacts[val].serving_size}</li>
                    <li>Calories: {props.foodFacts[val].Calories}</li>
                    <li>Protein: {props.foodFacts[val].Protein}</li>
                    <li>Total Carbohydrate: {props.foodFacts[val]["Total Carbohydrate"]}</li>
                    <li>Total Fat: {props.foodFacts[val]["Total Fat"]}</li>
                </ul>
            </div>
        </div>
    )

}
